import { CART_ADD_ITEM } from "../constants/cartContants";

export const cartReducer = (state = { cartItems: [] }, action) => {
  switch (action.type) {
    case CART_ADD_ITEM:
      const item = action.payload;
      const existItem = state.cartItems.find((x) => x.product === item.product);
      // console.log("cartReducer existItem: ");
      // console.log(existItem ? true : false);
      
      if (existItem) {
        // console.log("cartReducer  item.qty eee: " + item.qty);
        // state.cartItems.map((x) =>
        //   console.log(x.product === existItem.product)
        // );

        return {
          ...state,
          cartItems: state.cartItems.map((x) =>
            x.product === existItem.product ? item : x
          ),
        };
      } else {
        // console.log("cartReducer NOT existItem: " + item);
        return { ...state, cartItems: [...state.cartItems, item] };
      }
    default:
      return state;
  }
};
